import { useEffect,useState } from "react";
import TaskCard from "../cards/Taskcard";


function QuestRegisterPage(){
    const [stateList, setStateList]=useState([]);
    const [cityData,setCityData]=useState([]);
    const [State_selected,setState_selected]=useState("");
    const [Quest,setQuest] = useState([]);
    const [QuestBackend,setQuestBackend]=useState([]);
    useEffect(()=>{
        fetchStates();
    },[]);

    
    
    function AddToQuest(task){

        setQuest(...Quest,
            {"Name":task.TaskName,"city":task.City});
        setQuestBackend(...QuestBackend,task._id);


    }

    function deleteTask(task){
    }
    
    function taskRender(task){
        const t1={...task}
        delete t1._id

        return t1
    }

    async function citiesFetch(state){

        setState_selected(state);
        console.log(state);
        try{
          const response=  await fetch("/QuestRegisterPage_cities",{
                method:"POST",
                headers: {
                    'Content-Type': 'application/json'
                  },
                body:JSON.stringify({"state":state})
            })

            if(response.ok){
                const cities=await response.json()
                setCityData(cities);
                console.log("citydata:",cityData);

            }

        }
        catch(err){
            console.log("error:",err);
        }
    }

    async function fetchStates(){
        try{
            const response = await fetch("/QuestRegisterPage_state",{
                methods:"GET",
                
            }) 
            const stateData = await response.json();
            setStateList(stateData);
            console.log(stateData);
        }
        catch(error){
            console.log("error",error);
        }

    }

    return (
        <div className="flex h-screen">
        <div className="w-1/5 bg-gray-200 p-4 h-full flex-col">
            <h2>States List</h2>
            <div className="mt-2 p-1 flex-col h-full overflow-y-scroll">
            {   
                stateList.map((state, index) => (
                    <div key={index} className="p-1 cursor-pointer" onClick={()=>citiesFetch(state)}>
                        <p>{state}</p>
                    </div>
                ))
            }
            </div>
        </div>
        <div className="w-full bg-gray-300 p-4 h-full">
            <h2>Availabe tasks in Cities: {State_selected}</h2>
            <div>
                {
                    cityData.map((cityEntry,index)=>(


                        <div>
                            <h2>{cityEntry._id}</h2>
                            <div className="flex gap-4  mt-4 mb-4">
                            {
                            
                            cityEntry.entries.map((task)=>(
                                
                                
                                <TaskCard data={taskRender(task)} onClick={()=>AddToQuest(task)}></TaskCard>
                                
                             ))

                             }
                             </div>
                        </div>


                    ))
                }
            </div>
            
            </div>
        <div className="w-1/4 bg-gray-200 p-4 h-full">
            <h2>Quest Creation</h2>
            <div>
            { Quest.map((task)=>( 
              <li className="cursor-pointer flex justify-between px-2 py-1 w-full" >{task.Name} 
                <div className="flex">
                  <p className="text-large text-black-500 ">{task.city}</p>
                <IoClose className="mt-1 z-20" onclick={()=>deletetask(task)}/>
                </div>
              </li>
              
            ))}


            </div>
                
            <button
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
            
          >
            Create Quest
          </button>
        
        </div>
      </div>
    )



}



export default QuestRegisterPage;
