import { IoTrophy } from "react-icons/io5";
import { useUser } from "../Context/UserContext";
import { useEffect, useState } from "react";
import QuestCard from "../cards/QuestCard";
function Dashboard({loggedin}){
    
    
    
    const [user,setUser]=useState(null);
    const[Quests,setQuests]=useState([]);

    useEffect(()=>{
        fetchUserInfo();
    },[])
      const fetchUserInfo=async()=>{
        const val=JSON.parse(localStorage.getItem("user"));
        console.log(val.token);
        try{
        const response=await fetch("/Dashboard_getUser",{
            method:"GET",
            headers: {
                'Content-Type': 'application/json',
                "Authorization":`Bearer ${val.token}`
              },
              
        })  
            if(response.ok){
                const data=await response.json()
                console.log(data);
                setUser(data);
                console.log(user);
                fetchQuest(data.state);
            }
            
        }
        catch(err){
            console.log("errr",err);
        }


        

      }


      const fetchQuest=async(state)=>{
        const val = JSON.parse( localStorage.getItem("user"));
        try{
          const response=  await fetch("/Dashboard_quests",{
                method:"POST",
                headers:{
                    'Content-Type': 'application/json',
                    "Authorization":`Bearer ${val.token}`
                },

                body:JSON.stringify({"State":state})


            })
            if(response.ok){
                const data= await response.json();
                setQuests(data);
            }
        }
        catch(err){
            console.log("error found",err);
        }
      }
      

   
    return (
        <div className="flex h-screen">
      {/* Sidebar */}
      <div className="bg-gray-800 text-white w-1/4 p-4">
        <h2 className="text-lg font-bold mb-4">User Information</h2>
        <div>
          <p>Name: {user?user.Name:""}</p>
          <p>Email: {user?user.email:""}</p>
          {/* Add more user information as needed */}
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 p-4">
        {/* Search Bar */}
        <div className="mb-4">
          <input type="text" placeholder="Search quests..." className="w-full px-4 py-2 border border-gray-300 rounded-md" />
        </div>

        {/* Quest Cards */}
        <div className="grid grid-cols-3 gap-4">
            {Quests.map((quest)=>(
                <QuestCard quest={quest}></QuestCard>
                
            ))}
          
        </div>
      </div>
    </div>

    )

}

export default Dashboard;
