import React from 'react';
import { useParams } from 'react-router-dom'; // If using React Router for routing

const QuestDetailsPage = () => {
  // Use useParams to extract the quest ID from the URL
  const { questId } = useParams();
  const [selectedImage, setSelectedImage] = useState(quest.images[0]);

  // Function to handle image click and update selectedImage
  const handleImageClick = (image) => {
    setSelectedImage(image);
  };

  // Mock data for the quest (replace with actual data)
  const quest = {
    _id: '65d5125bad4ac57a779b0700',
    QuestId: 'c0cd3972-84ed-4537-837e-069d7c97524e',
    QuestName: 'one',
    itinerary: '5asdasd',
    charges: '5555',
    details: 'sadasda',
    length: '7',
    tasks: {
      state: 'five'
    },
    images: [
      'image1.jpg',
      'image2.jpg',
      'image3.jpg'
    ]
  };

  // Render the quest details
  return (
    <div className="container mx-auto p-8">
      <div className="max-w-6xl mx-auto bg-white shadow-md rounded-lg overflow-hidden">
        {/* Quest Images */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          {quest.images.map((image, index) => (
            <img key={index} className="h-64 w-full object-cover" src={image} alt={`Quest Image ${index + 1}`} />
          ))}
        </div>
        {/* Quest Details */}
        <div className="p-8">
          <h2 className="text-3xl font-bold mb-2">{quest.QuestName}</h2>
          <p className="text-gray-700 mb-4">{quest.itinerary}</p>
          <p className="text-gray-700 mb-4">Charges: {quest.charges}</p>
          <p className="text-gray-700 mb-4">{quest.details}</p>
          <p className="text-gray-700 mb-4">Length: {quest.length} days</p>
          <p className="text-gray-700 mb-4">Tasks: {quest.tasks.state}</p>
        </div>
      </div>
    </div>
  );
};

export default QuestDetailsPage;
